import React, { useState, useEffect } from "react";
import { createCard, readDeck } from "../utils/api/index.js";
import { Link, useHistory, useParams } from "react-router-dom";
import CardForm from "./CardForm";

export default function AddCard() {
  const [deck, setDeck] = useState();
  const history = useHistory();
  const { deckId } = useParams();

  useEffect(() => {
    const abortController = new AbortController();
    getDeck(deckId, abortController);
    return () => abortController.abort();
  }, [deckId]);

  async function getDeck(deckId, abortController) {
    try {
      const screen = await readDeck(deckId, abortController.signal);
      setDeck(screen);
    } catch (error) {
      console.log(error);
    }
  }

  const formData = new FormData(e.target);
  async function submitHandler(e) {
    e.preventDefault();

    console.log(formData.get("front"));
    const card = {
      front: formData.get("front"),

      back: formData.get("back"),
    };
    try {
      await createCard(deckId, card);
      alert("Success");
    } catch (error) {
      console.log(error);
    }
    window.location.reload();
  }

  return (
    <div>
      {deck && (
        <div>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li class="breadcrumb-item">
                <Link to="/">Home</Link>
              </li>
              <li class="breadcrumb-item">
                <Link to={`/decks/${deckId}`}>{deck.name}</Link>
              </li>
              <li class="breadcrumb-item active" aria-current="page">
                Add Card
              </li>
            </ol>
          </nav>
          <h2>{deck.name}: Add Card</h2>
          <CardForm formData={formData} submitHandler={submitHandler} />
        </div>
      )}
    </div>
  );
}
