import React, { useEffect, useState } from "react";
import { PlusIcon, TrashIcon, EyeIcon, BookIcon } from "@primer/octicons-react";
import { listDecks } from "../utils/api/index.js";
// import decks from "../data/db.json";

export default function DeckList() {
  const [decks, setDecks] = useState([]);
  useEffect(() => {
    async function loadData() {
      try {
        const screen = await listDecks();
        setDecks(screen);
      } catch {
        error;
        console.log("decks error");
      }
    }
    loadData();
  });

  console.log(decks);

  const ListDeck = decks.map((deck) => {
    return (
      <div>
        <button type="button" class="btn btn-secondary btn-lg">
          <PlusIcon size={24} />
          Create Deck
        </button>
        <div class="card w-75">
          <div class="card-body">
            <h5 class="card-title">{deck.name}</h5>
            <h6 className="card-subtitle mb-2 text-muted">
              {deck.cards.length}
            </h6>
            <p class="card-text">{deck.description}</p>
            <a href="#" class="btn btn-secondary">
              <EyeIcon size={24} /> View
            </a>
            <a href="#" class="btn btn-primary" style={{ marginLeft: "10px" }}>
              <BookIcon size={24} />
              Study
            </a>
            <a href="#" class="btn btn-danger" style={{ float: "right" }}>
              <TrashIcon size={24} />
            </a>
          </div>
        </div>
        ;
      </div>
    );
  });
}
