import React, { useEffect, useState } from "react";
import {
  PlusIcon,
  TrashIcon,
  EyeIcon,
  BookIcon,
  PencilIcon,
  PlusCircleIcon,
} from "@primer/octicons-react";
import { readDeck, deleteDeck, listCards } from "../utils/api/index.js";
import { Link, useParams, useHistory } from "react-router-dom";
import DeckList from "./DeckList.js";

export default function Deck() {
  const [deck, setDeck] = useState();
  const { deckId } = useParams();

  useEffect(() => {
    async function loadData() {
      try {
        const screen = await readDeck(deckId);
        setDeck(screen);
      } catch (error) {
        console.log(error);
      }
    }
    loadData();
  }, [deckId]);

  async function deleteHandler(card) {
    if (window.confirm("Are you sure you want to delete this deck?")) {
      try {
        await deleteDeck(card);
        const screen = await listDecks();
        setDecks(screen);
      } catch (error) {
        console.log(error);
      }
    }
  }

  return (
    <>
      {deck && (
        <>
          <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <Link to="/">Home</Link>
              </li>
              <li class="breadcrumb-item active" aria-current="page">
                {deck.name}
              </li>
            </ol>
          </nav>
          <div>
            <div class="card w-75">
              <div class="card-body">
                <h2 class="card-title">{deck.name}</h2>
                <p class="card-text">{deck.description}</p>
                <Link to={`/decks/${deck.id}`} class="btn btn-secondary">
                  <PencilIcon size={24} /> Edit
                </Link>
                {/* Change icon update link */}

                <Link
                  to="#"
                  class="btn btn-primary"
                  style={{ marginLeft: "10px" }}
                >
                  <BookIcon size={24} />
                  Study
                </Link>
                {/* Change icon update link*/}

                <Link
                  to="#"
                  class="btn btn-primary"
                  style={{ marginLeft: "10px" }}
                >
                  <PlusCircleIcon size={24} />
                  Add Cards
                </Link>
                {/* Change icon update link*/}

                <Link
                  to="/"
                  class="btn btn-danger"
                  style={{ float: "right" }}
                  onClick={() => deleteHandler(deck.id)}
                >
                  <TrashIcon size={24} />
                </Link>
              </div>
            </div>
          </div>
          <h2>Cards</h2>
          {deck.cards.length > 0 &&
            deck.cards.map((card) => (
              <div class="row">
                <div class="col-sm-6">
                  <div class="card">
                    <div class="card-body">
                      <p class="card-text">{card.front}</p>
                    </div>
                  </div>
                </div>
                <div class="col-sm-6">
                  <div class="card">
                    <div class="card-body">
                      <p class="card-text">{card.back}</p>
                      <div class="icons" style={{ float: "right" }}>
                        <Link
                          to="#"
                          class="btn btn-primary"
                          style={{ size: "24px" }}
                        >
                          Edit
                        </Link>
                        <button
                          to="#"
                          class="btn btn-danger"
                          style={{ marginLeft: "10px" }}
                        >
                          <TrashIcon size={24} />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
        </>
      )}
    </>
  );
}
