import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
// import { PlusIcon, TrashIcon, EyeIcon, BookIcon } from "@primer/octicons-react";
// import { listDecks, deleteDeck } from "../utils/api/index.js";

import { Link } from "react-router-dom";
import { HomeFillIcon } from "@primer/octicons-react";
import DeckList from "./DeckList";
import { updateCard, readCard, createCard } from "../utils/api/index.js";

export default function EditDeck() {
  const [card, setCard] = useState();
  const [frontCard, setFrontCard] = useState();
  const { deckId, cardId } = useParams();

  const history = useHistory();

  useEffect(() => {
    const abortController = new AbortController();
    getCard(deckId, abortController);
    return () => abortController.abort();
  }, [deckId]);

  async function getCard(deckId, abortController) {
    try {
      const response = await readCard(deckId, abortController.signal);
      setCard(response);
    } catch (error) {
      console.log(error);
    }
  }
  async function updateCard(e) {
    e.preventDefault();
    const formData = new FormData(e.target);
    console.log(formData.get("front"));
    const card = {
      front: formData.get("front"),

      back: formData.get("back"),
    };
    try {
      await createCard(deckId, card);
      alert("Success");
    } catch (error) {
      console.log(error);
    }
    window.location.reload();
  }

  return (
    <div>
      {card && (
        //   Breadcrumb - displaying path to this page
        <div>
          <div>
            <nav aria-label="breadcrumb">
              <ol class="breadcrumb">
                <li class="breadcrumb-item">
                  <Link to="/">
                    <HomeFillIcon size={16} />
                    Home
                  </Link>
                </li>
                <li class="breadcrumb-item active" aria-current="page">
                  <Link to={`/decks/${deckId}/cards/${cardId}/edit`}>
                    Deck {card.name}
                  </Link>
                </li>
                <li className="breadcrumb-item active" aria-current="page">
                  Edit Card
                </li>
              </ol>
            </nav>
          </div>

          <h1>Edit Card</h1>
          <form onSubmit={updateCard}>
            <div class="form-group">
              <label htmlFor="front">Front</label>
              <input
                type="text"
                name="front"
                className="form-control"
                id="front"
                rows="2"
                defaultValue={card.front}
              />
            </div>
            <div className="form-group">
              <label htmlFor="back">Back</label>
              <textarea
                className="form-control"
                name="back"
                id="back"
                rows="2"
                defaultValue={card.back}
              ></textarea>
            </div>
            <Link
              to={`/decks/${deckId}/cards`}
              type="button"
              className="btn btn-secondary"
            >
              Cancel
            </Link>
            <button
              type="submit"
              className="btn btn-primary"
              style={{ marginLeft: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
      )}
    </div>
  );
}
