import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useHistory } from "react-router";
import { HomeFillIcon } from "@primer/octicons-react";
import { updateCard, readCard, readDeck } from "../utils/api/index.js";

export default function EditDeck() {
  const [deck, setDeck] = useState();
  const [frontCard, setFrontCard] = useState("");
  const [backCard, setBackCard] = useState("");
  const { deckId, cardId } = useParams();
  const history = useHistory();

  useEffect(() => {
    const abortController = new AbortController();
    getDeck(deckId, abortController);
    getCard(cardId, abortController);
    return () => abortController.abort();
  }, []);

  async function getDeck(deckId, abortController) {
    try {
      let response = await readDeck(deckId, abortController.signal);
      setDeck(response);
    } catch (error) {
      console.log(error);
    }
  }

  async function getCard(cardId, abortController) {
    try {
      const { front, back } = await readCard(cardId, abortController.signal);
      setFrontCard(front);
      setBackCard(back);
    } catch (error) {
      console.log(error);
    }
  }

  async function updateCard(e) {
    e.preventDefault();
    // const formData = new FormData(e.target);
    // console.log(formData.get("frontCard"));
    const card = {
      deckId,
      id: cardId,
      front: frontCard,
      back: backCard,
    };
    try {
      await updateCard(card);
      history.push(`/decks/${deckId}`);
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div>
      <div>
        {/* // Breadcrumb - displaying path to this page */}
        <div>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">
                  <HomeFillIcon size={16} />
                  Home
                </Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                <Link to={`/decks/${deckId}`}>Deck {deck.name}</Link>
              </li>
              <li className="breadcrumb-item active" aria-current="page">
                Edit Card {cardId}
              </li>
            </ol>
          </nav>
        </div>
        <h1>Edit Card</h1>
        <form onSubmit={updateCard}>
          <div className="form-group">
            <label htmlFor="frontCard">Front</label>
            <textarea
              type="text"
              name="frontCard"
              className="form-control"
              id="frontCard"
              rows="2"
              defaultValue={frontCard}
              onChange={(e) => setFrontCard(e.target.value)}
            ></textarea>
          </div>
          <div className="form-group">
            <label htmlFor="back">Back</label>
            <textarea
              className="form-control"
              name="back"
              id="back"
              rows="2"
              defaultValue={backCard}
              onChange={(e) => setBackCard(e.target.value)}
            ></textarea>
          </div>
          <Link
            to={`/decks/${deckId}`}
            type="button"
            className="btn btn-secondary"
          >
            Cancel
          </Link>
          <button
            type="submit"
            className="btn btn-primary"
            style={{ marginLeft: "10px" }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
